pragma solidity ^0.8.18;

import {IDiamondLoupe} from "hardhat-deploy/solc_0.8/diamond/interfaces/IDiamondLoupe.sol";
import {UsingDiamondOwner, IDiamondCut} from "hardhat-deploy/solc_0.8/diamond/UsingDiamondOwner.sol";
import "hardhat-deploy/solc_0.8/diamond/libraries/LibDiamond.sol";
import {SusuwatariStorage, StorageHandler} from "./SusuwatariStorage.sol";
import "./ILeafWalletRegistryActivate.sol";


contract InitFacet is UsingDiamondOwner, StorageHandler {
  
  constructor(){
        LibDiamond.setContractOwner(msg.sender);
        
    }
    
    function init() external onlyOwner {

        SusuwatariStorage storage susu = susu();


        if (susu.isInitialized) return;

        ds().supportedInterfaces[type(IDiamondCut).interfaceId] = true;
        ds().supportedInterfaces[type(IDiamondLoupe).interfaceId] = true;
        
        ds().supportedInterfaces[type(ILeafWalletRegistryActivate).interfaceId] = true;

        susu.isInitialized = true;
    }
}","keccak256":"0x16c6a961bfd0ac69e0663ae7cceb49e0c3ad29544d5b347d1f079fec57db3359","license":"MIT"},"contracts/SusuwatariStorage.sol":{"content":"//SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

import {LibDiamond} from "hardhat-deploy/solc_0.8/diamond/libraries/LibDiamond.sol";

bytes32 constant SUSUWATARI_STORAGE_POSITION = keccak256("susuwatari.contract.storage");
bytes32 constant LEAFWALLET_STORAGE_POSITION = keccak256("leafwallet.contract.storage");

struct OwnableStorage {

    address _owner;
}

struct SusuwatariStorage {
    

  bool isInitialized;
}

struct LeafWalletStorage {
    mapping(address => address) _deviceOwner;
    mapping(address => address[]) _ownerDevices;
    mapping(address => uint256) _deviceActivationCodes;
}

contract StorageHandler {
    function susu() internal pure returns (SusuwatariStorage storage cs) {
        bytes32 position = SUSUWATARI_STORAGE_POSITION;
        assembly {
           cs.slot := position
        }
    }

    function os() internal pure returns (OwnableStorage storage cs) {
        bytes32 position = keccak256("ownable.contract.storage");
        assembly {
           cs.slot := position
        }
    }
    
    function ds() internal pure returns (LibDiamond.DiamondStorage storage) {
        return LibDiamond.diamondStorage();
    }

    
    function lw() internal pure returns (LeafWalletStorage storage cs) {
        bytes32 position = keccak256("leafwallet.contract.storage");
        assembly {
           cs.slot := position
        }
    }
}","keccak256":"0xdf2fb80f5b3fd1b3fbaa4481353d252834072978fd4eb242b4ab23f894bafd16","license":"MIT"},"hardhat-deploy/solc_0.8/diamond/UsingDiamondOwner.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./libraries/LibDiamond.sol";

contract UsingDiamondOwner {
    modifier onlyOwner() {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        require(msg.sender == ds.contractOwner, "Only owner is allowed to perform this action");
        _;
    }
}
